declare module "slim-scroll" {
    export default function SlimScroll(element: any, options?: SlimScrollOptions): void;

    export interface ISlimScroll{
        resetValues(): void;
        init(): void;
    }

    export interface SlimScrollOptions {
        wrapperClass?: String;
        scrollBarClass?: String;
        scrollBarContainerClass?: String;
        scrollBarContainerSpecialClass?: String;
        scrollBarMinHeight?: number;
        scrollBarFixedHeight?: number;
        keepFocus?: boolean;
    }
}
